import { addProductImages, addVariantImages } from "./addImagePaths.js";
import { sortProducts } from "./sortProducts.js";

function groupProductsWithVariants(products) {
    const productsByName = {};

    products.forEach((product) => {
      const productName = product.productName;
      const variant = {
        sku: product.sku,
        'option1Value': product.option1Value,
        'option2Value': product.option2Value,
        images: []
      };

      addVariantImages(variant);

      if (!productsByName[productName]) {
        addProductImages(product);

        product.productDescription = shortenDescription(product.productDescription);
        product.productDescription = removeHTMLSpans(product.productDescription);
        product.productDescription = cropAtBreak(product.productDescription);

        const [subfolder1, subfolder2] = product.sku.split('-');
        const baseSku = `${subfolder1}-${subfolder2}`;
        const use1x2Grid = false;

        // set a flag if the product image is the header image and is a jpg
        const jpgRegex = /\.(jpg|jpeg)$/i;
        if (!product.hasJpgLifestyleImage && jpgRegex.test(variant.images[0])) {
          product.hasJpgImage = true;
        }

        productsByName[productName] = {
          ...product,
          baseSku,
          use1x2Grid,
          variants: [],
        };
      }

      productsByName[productName].variants.push(variant);
    });

    const productsWithVariants = Object.values(productsByName);
    const sortedProducts = sortProducts(productsWithVariants);
    return sortedProducts;
};   

function shortenDescription(description) {
  const regex = /<p>(.*?)<\/p>/;
  const matches = description.match(regex);
  const firstParagraph = matches && matches[1];
  return firstParagraph || description;
}

function removeHTMLSpans(html) {
  const regex = /<\/?span[^>]*>/gi;
  const result = html.replace(regex, '');
  return result;
}

function cropAtBreak(inputString) {
  const regex = /<br\s*\/?>/;
  const trimmedString = inputString.split(regex)[0];
  return trimmedString;
}

export { groupProductsWithVariants };

function isJpg(imagePath) {

    // check if the image is a jpg format
    const jpgRegex = /\.(jpg|jpeg)$/i;

    return jpgRegex.test(imagePath);
}

const lightColors = [
    'honeydew',
    'lightgoldenrodyellow',
    'linen',
    'ivory',
    'aliceblue',
    'lightcyan'
];

const brightColors = [
    'lemonchiffon',
    'wheat',
    'khaki',
    'aquamarine',
    'bisque',
    'palegoldenrod',
    'burlywood',
    'peachpuff'
];

function hash(imagePath, colorOptions) {
    // Use the imagePath to create a consistent random index for the colorOptions array
    const hash = imagePath.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
    const colorIndex = hash % colorOptions.length;
    const color = colorOptions[colorIndex];
    return color;
}

function lifestyleBgColorHelper(imagePath) {
    let colorOptions = brightColors;
    if (isJpg(imagePath)) {
        colorOptions = lightColors;
    } 

    const color = hash(imagePath, colorOptions);

    return color;
}

function productBgColorHelper(imagePath) {
    if (typeof imagePath === 'undefined') {
        return 'none';
    }
    let colorOptions = brightColors;
    if (isJpg(imagePath)) {
        colorOptions = ['white'];
    } 

    const color = hash(imagePath, colorOptions);

    return color;
}

  export { lifestyleBgColorHelper, productBgColorHelper };

function toCamelCase(str) {
    return str
      .replace(/\s*\([^)]*\)\s*/g, ' ') // Remove parentheses and their contents, and condense spaces to a single space
      .trim() // Remove leading and trailing spaces
      .toLowerCase() // Convert the entire string to lowercase
      .replace(/(?:^\w|[A-Z]|\b\w|\s+)/g, function (match, index) {
        if (+match === 0) return ''; // or if (/\s+/.test(match)) for white spaces
        return index === 0 ? match.toLowerCase() : match.toUpperCase();
      });
  }

  function renameProperties(obj) {
    for (const prop in obj) {
      if (obj.hasOwnProperty(prop)) {
        const camelCasedProp = toCamelCase(prop);
        if (prop !== camelCasedProp) {
          obj[camelCasedProp] = obj[prop];
          delete obj[prop];
        }
      }
    }
    return obj;
  }

export { renameProperties };

import fs from 'fs';
import { parse } from './node_modules/csv-parse/lib/sync.js';

// Desired order of product types
const desiredOrder = JSON.parse(fs.readFileSync('product_type_order.json', 'utf8'));

// Function to parse the CSV file and create a mapping of SKUs to sales totals
function parseSalesTotalsCSV(filePath) {
  const data = fs.readFileSync(filePath, 'utf8');
  const records = parse(data, { columns: true });
  const salesTotals = {};

  for (const row of records) {
    const { baseSku, sales } = row;
    salesTotals[baseSku] = parseFloat(sales);
  }

  return salesTotals;
}

// Custom sort function
function customProductSort(salesTotals, a, b) {
    const orderA = desiredOrder.indexOf(a.productType);
    const orderB = desiredOrder.indexOf(b.productType);

    // Test the productType
    if (orderA !== orderB) {
      return orderA - orderB;
    }

    const salesTotalA = salesTotals[a.baseSku] || 0;
    const salesTotalB = salesTotals[b.baseSku] || 0;
    return salesTotalB - salesTotalA;
}

// Custom variant sort function
function customVariantSort(a, b) {
    const option1ValueA = a.option1Value;
    const option1ValueB = b.option1Value;

    if (option1ValueA < option1ValueB) { 
        return -1;
    } else if (option1ValueA > option1ValueB) {
        return 1
    } else { // nothing to split them
        return 0;
    }
}


function sortProducts(products) {
    try {
        // Parse the CSV file and created a salesTotals object
        // e.g., { 'sku1': 100, 'sku2': 50, 'sku3': 200, ... }
        const csvFilePath = './sales_by_baseSku.csv';
        const salesTotals = parseSalesTotalsCSV(csvFilePath);

        // Sort the products array using the custom sort function and the salesTotals data
        products.sort((a, b) => customProductSort(salesTotals, a, b));

        // Sort the variants array on each product
        products.forEach(product => {
            product.variants.sort((a, b) => customVariantSort(a, b));
        });

    } catch (error) {
        console.error('Error reading CSV file:', error);
    }

    // Now the products array is sorted by the desired productType order and sales total
    return products;
}

export { sortProducts };

